INSERT INTO `notice` VALUES ('239', '2', '5', '3', null, 'Double Stockage d\'EXP', '4', '0', '<pic>http://pull.clashfordawn.com/160105notice04fr.jpg</pic><T t=\"Double Stockage d\'EXP\"></T><B></B><T t=\"Bonnes nouvelles! Après avoir atteint à la limite de niveau (\"></T><T t=\"Niv.79\" c=\"ffe9af46\"></T><T t=\"), vous pouvez sauvegarder l\'EXP gagné. L\'EXP que vous pouvez sauvegarder, sera \"></T><T t=\"doublé \" c=\"ffe9af46\"></T><T t=\"par EXP de votre niveau présent! L\'EXP sauvegardé peut être utilisé pour \"></T><T t=\"Héritage d\'EXP\" c=\"ffe9af46\"></T><T t=\" à vos mercenaires, ainsi qu\'en augementant votre niveau de caractère quand la limite de niveau a été augmentée.\"></T><B></B><T t=\"Donc, ne renoncez jamais pour gagner toute de chance d\'EXP, même après avoir atteint à la limite de niveau!\"></T>', '<pic>http://pull.clashfordawn.com/160105notice04fr.jpg</pic><T t=\"Double Stockage d\'EXP\"></T><B></B><T t=\"Bonnes nouvelles! Après avoir atteint à la limite de niveau (\"></T><T t=\"Niv.79\" c=\"ffe9af46\"></T><T t=\"), vous pouvez sauvegarder l\'EXP gagné. L\'EXP que vous pouvez sauvegarder, sera \"></T><T t=\"doublé \" c=\"ffe9af46\"></T><T t=\"par EXP de votre niveau présent! L\'EXP sauvegardé peut être utilisé pour \"></T><T t=\"Héritage d\'EXP\" c=\"ffe9af46\"></T><T t=\" à vos mercenaires, ainsi qu\'en augementant votre niveau de caractère quand la limite de niveau a été augmentée.\"></T><B></B><T t=\"Donc, ne renoncez jamais pour gagner toute de chance d\'EXP, même après avoir atteint à la limite de niveau!\"></T>', '<pic>http://pull.clashfordawn.com/160105notice04fr.jpg</pic><T t=\"Double Stockage d\'EXP\"></T><B></B><T t=\"Bonnes nouvelles! Après avoir atteint à la limite de niveau (\"></T><T t=\"Niv.79\" c=\"ffe9af46\"></T><T t=\"), vous pouvez sauvegarder l\'EXP gagné. L\'EXP que vous pouvez sauvegarder, sera \"></T><T t=\"doublé \" c=\"ffe9af46\"></T><T t=\"par EXP de votre niveau présent! L\'EXP sauvegardé peut être utilisé pour \"></T